using namespace std;
using namespace __gnu_pbds;

template <class T> using ordered_set = tree<T, null_type, less<T>, rb_tree_tag, tree_order_statistics_node_update>;

void sol() {
    int n;
    cin >> n;
    vector<int> becon(n), yidx(n);
    vector<tuple<int, int, int>> b(n), c(n);

    for (int i = 0; i < n; ++i) {
		int l, r;
		cin >> l >> r;
        b[i] = {l, r * -1, i};
        c[i] = {r, l * -1, i};
    }

    sort(b.begin(), b.end());
    sort(c.begin(), c.end());

	for (int i = 0; i < n; ++i)
