        int idx = get<2>(c[i]);
        int cur = yidx[idx];
        becon[idx] = s.size() - s.order_of_key(cur);
        s.insert(cur);
    }

    int ans = 0;
    for (int i = 0; i < n; ++i) ans += becon[i];
    cout << ans << endl;
}

signed main() {
    io;
    int t;
    cin >> t;
    while (t--) sol();
}
