#include <bits/stdc++.h>
#define int long long
#define pii pair<int, int>
#define ff first
#define ss second

using namespace std;

const int maxn = 1e3 + 10;
const int inf = 1e13;

int n, m;
vector<pii> g[maxn];
int bike[maxn];

vector<pii> g2[maxn];
int dist2[maxn][maxn];

int dist[maxn];

void sol() {
    // åˆå§‹åŒ– & è®€å–è¼¸å…¥
    cin >> n >> m;
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            dist2[i][j] = inf;
        }
    }
    for (int i = 0; i <= n; i++) {
        g[i].clear();
        g2[i].clear();
    }
    for (int i = 0; i < m; i++) {
        int a, b, w;
        cin >> a >> b >> w;
        g[a].push_back({b, w});
        g[b].push_back({a, w});
    }
    for (int i = 1; i <= n; i++) {
        cin >> bike[i];
    }
    // åœ¨åŸå§‹çš„åœ–ä¸Šæ±‚æ¯å€‹é»å°å¾—æœ€çŸ­è·¯
    for (int v = 1; v <= n; v++) {
        // åˆå§‹åŒ–
        for (int i = 1; i <= n; i++) {
            dist[i] = inf;
        }
        // ä½¿ç”¨ dijkstra æ¼”ç®—æ³•
        priority_queue<pii, vector<pii>, greater<pii>> pq;
        pq.push({0, v});
        dist[v] = 0;
        while (pq.size()) {
            auto now = pq.top(); pq.pop();
            if (now.ff > dist[now.ss]) continue;
            for (auto other : g[now.ss]) {
                if (dist[other.ff] > dist[now.ss] + other.ss) {
                    dist[other.ff] = dist[now.ss] + other.ss;
                    pq.push({dist[other.ff], other.ff});
                }
            }
        }
        // å»ºé‚Šåœ¨æ–°çš„åœ–ä¸Š
        for (int i = 1; i <= n; i++) {
            if (i == v) continue;
            g2[v].push_back({i, dist[i] * bike[v]});
        }
    }
    // åˆå§‹åŒ– (æº–å‚™åœ¨æ–°çš„åœ–ä¸Šåšæœ€çŸ­è·¯)
    for (int i = 1; i <= n; i++) {
        dist[i] = inf;
    }
    // ä½¿ç”¨ dijkstra æ¼”ç®—æ³•
    priority_queue<pii, vector<pii>, greater<pii>> pq;
    pq.push({0, 1});
    dist[1] = 0;
    while (pq.size()) {
        auto now = pq.top(); pq.pop();
        if (now.ff > dist[now.ss]) continue;
        for (auto other : g2[now.ss]) {
            if (dist[other.ff] > dist[now.ss] + other.ss) {
                dist[other.ff] = dist[now.ss] + other.ss;
                pq.push({dist[other.ff], other.ff});
            }
        }
    }
    // è¼¸å‡ºè§£
    cout << dist[n] << endl;
}

signed main() {
    int t;
    cin >> t;
    while (t--) sol();
}
```

