#include <bits/stdc++.h>
using namespace std;
#define int short
#define endl "\n"

const int MAXN = 1e2 + 3;
const int INF = 32000;

int mp[MAXN][MAXN];
int dp[MAXN][MAXN][MAXN][MAXN];

signed main() {
    ios_base::sync_with_stdio(0); cin.tie(0); cout.tie(0);
    int n, m;
    cin >> n >> m;
    // æŠŠé‚Šç•Œéƒ½è¨­å®šç‚º -INF
    for (int i = 0; i <= n + 1; i++) {
        for (int j = 0; j <= m + 1; j++) {
            for (int k = 0; k <= n + 1; k++) {
                for (int l = 0; l <= m + 1; l++) {
                    if (i == 0 || i == n+1 || j == 0 || j == m+1 || k == 0 || k == n+1 || l == 0 || l == m+1) {
                        dp[i][j][k][l] = -INF;
                    }
                }
            }
        }
    }
    // åˆå§‹ç‹€æ…‹
    dp[0][0][0][0] = 0;
    dp[0][1][0][1] = 0;
    dp[1][0][1][0] = 0;
    dp[0][1][1][0] = 0;
    dp[1][1][1][1] = 0;
    // è¼¸å…¥æ•´å¼µåœ°åœ–
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            cin >> mp[i][j];
        }
    }
    // DP
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            for (int k = 1; k <= n; k++) {
                for (int l = 1; l <= m; l++) {
                    // ä¸€é»é»å‰ªæ
                    if (i + j != k + l) continue;
                    if (mp[i][j] == -1 || mp[k][l] == -1) {
                        dp[i][j][k][l] = -INF;
                        continue;
                    }
                    // è½‰ç§»
                    dp[i][j][k][l] = max({
                        dp[i-1][j][k-1][l],
                        dp[i-1][j][k][l-1],
                        dp[i][j-1][k-1][l],
                        dp[i][j-1][k][l-1],
                    });
                    // æ‹¿åˆ°å¯¶è—å¢åŠ åˆ†æ•¸
                    dp[i][j][k][l] += (mp[i][j] == 1) + (mp[k][l] == 1) * (i != k || j != l);
                }
            }
        }
    }
    // è¼¸å‡ºç­”æ¡ˆ
    cout << max(dp[n][m][n][m], (short)0) << endl;
}
```
